import React from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import { Box, Typography } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import QueryStatsIcon from '@mui/icons-material/QueryStats';
import ChatIcon from '@mui/icons-material/Chat';
import NewspaperIcon from '@mui/icons-material/Newspaper';
import GroupIcon from '@mui/icons-material/Group';
import MusicNoteIcon from '@mui/icons-material/MusicNote'; // Remplacement pour TikTok
import FacebookIcon from '@mui/icons-material/Facebook';
import EmailIcon from '@mui/icons-material/Email';
import PublicIcon from '@mui/icons-material/Public';

const drawerWidth = 240;
const collapsedWidth = 70;

const Sidebar = ({ open, toggleSidebar }) => {
  const icons = [
    <HomeIcon />,
    <QueryStatsIcon />,
    <ChatIcon />,
    <NewspaperIcon />,
    <EmailIcon />,
    <GroupIcon />,
    <FacebookIcon />,
    <MusicNoteIcon />,
    <EmailIcon />,
    <PublicIcon />
  ];

  return (
    <Drawer
      variant="persistent"
      open={open}
      sx={{
        width: open ? drawerWidth : collapsedWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: open ? drawerWidth : collapsedWidth,
          boxSizing: 'border-box',
          overflowX: 'hidden',
          backgroundColor: '#212121', // Dark grey background
          color: 'white',
        },
      }}
    >
      <Box sx={{ padding: 2, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Typography variant="h6">Logo</Typography>
        <HomeIcon onClick={toggleSidebar} sx={{ cursor: 'pointer' }} /> // Using HomeIcon as a placeholder for toggle icon
      </Box>
      <List>
        {['Top Stocks', 'Data Bits', 'Reddit Mentions', 'News Mentions', '4chan Mentions', 'Instagram Followers', 'Facebook Followers', 'TikTok Followers', 'Threads Followers', 'Webpage Traffic'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton onClick={() => {/* Navigate or other actions */}}>
              <ListItemIcon>{icons[index]}</ListItemIcon>
              <ListItemText primary={text} sx={{ display: open ? 'block' : 'none' }} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Drawer>
  );
};

export default Sidebar;
