import React, { useState, useEffect } from 'react';

const StockPrice = ({ symbol, apiKey }) => {
  const [price, setPrice] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const apiURL = `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${symbol}&interval=5min&apikey=${apiKey}`;

    fetch(apiURL)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const latestDatetime = max(Object.keys(data['Time Series (5min)']));
        const latestPrice = data['Time Series (5min)'][latestDatetime]['4. close'];
        setPrice(latestPrice);
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, [symbol, apiKey]);  // Dependencies

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>{symbol} Price</h1>
      <p>${price}</p>
    </div>
  );
};

export default StockPrice;
