import React from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton, Box } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import logo from '../Side/Asset/cap.png';  // Assurez-vous que le chemin vers votre logo est correct

function Sidebar({ isOpen, toggleDrawer }) {
    return (
        <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%', bgcolor: 'background.paper' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', p: 1 }}>
                <img src={logo} alt="Logo" style={{ height: 40 }} />
                <IconButton onClick={() => toggleDrawer(!isOpen)}>
                    <MenuIcon />
                </IconButton>
            </Box>
            <Drawer anchor='left' variant="persistent" open={isOpen} sx={{ '& .MuiDrawer-paper': { width: 240 } }}>
                <List>
                    {['Top Stocks', 'Data Bits', 'Reddit Mentions', 'News Mentions', '4chan Mentions', 'Instagram Followers', 'Facebook Followers', 'TikTok Followers', 'Threads Followers'].map((text) => (
                        <ListItem button key={text}>
                            <ListItemText primary={text} />
                        </ListItem>
                    ))}
                </List>
            </Drawer>
        </Box>
    );
}

export default Sidebar;
