import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Drawer, List, ListItem, ListItemIcon, ListItemText, Button, Menu, MenuItem } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircle from '@mui/icons-material/AccountCircle';
import HomeIcon from '@mui/icons-material/Home';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import ChatIcon from '@mui/icons-material/Chat';
import AnnouncementIcon from '@mui/icons-material/Announcement';
import PublicIcon from '@mui/icons-material/Public';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import EmailIcon from '@mui/icons-material/Email';
import TrafficIcon from '@mui/icons-material/Traffic';

const Layout = ({ children }) => {
    const [sidebarOpen, setSidebarOpen] = useState(true);  // Sidebar initially open
    const [anchorEl, setAnchorEl] = useState(null);
    const isMenuOpen = Boolean(anchorEl);

    const handleSidebarToggle = () => {
        setSidebarOpen(!sidebarOpen);
    };

    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };

    const menuItems = [
        { text: 'Top Stocks', icon: <TrendingUpIcon /> },
        { text: 'Data Bits', icon: <HomeIcon /> },
        { text: 'Reddit Mentions', icon: <ChatIcon /> },
        { text: 'News Mentions', icon: <AnnouncementIcon /> },
        { text: '4chan Mentions', icon: <PublicIcon /> },
        { text: 'Instagram Followers', icon: <ThumbUpIcon /> },
        { text: 'Facebook Followers', icon: <ThumbUpIcon /> },
        { text: 'TikTok Followers', icon: <MusicNoteIcon /> },
        { text: 'Threads Followers', icon: <EmailIcon /> },
        { text: 'Webpage Traffic', icon: <TrafficIcon /> }
    ];

    return (
        <div>
            
            <Drawer
                variant="permanent"
                sx={{
                    width: sidebarOpen ? 240 : 60,
                    flexShrink: 0,
                    '& .MuiDrawer-paper': {
                        width: sidebarOpen ? 240 : 60,
                        boxSizing: 'border-box',
                        overflowX: 'hidden'  // Hide text when sidebar is closed
                    },
                }}
                open={sidebarOpen}
            >
                <List>
                    {menuItems.map((item, index) => (
                        <ListItem button key={item.text} onClick={handleSidebarToggle}>
                            <ListItemIcon>{item.icon}</ListItemIcon>
                            {sidebarOpen && <ListItemText primary={item.text} />}
                        </ListItem>
                    ))}
                </List>
            </Drawer>
            <main style={{ paddingLeft: sidebarOpen ? 240 : 60 }}>
                {children}
            </main>
        </div>
    );
};

export default Layout;
