import React from 'react';
import { Box, IconButton, Drawer, List, ListItemButton, ListItemIcon, ListItemText } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import DashboardIcon from '@mui/icons-material/Dashboard';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import CommentIcon from '@mui/icons-material/Comment';
import NewspaperIcon from '@mui/icons-material/Newspaper';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import InstagramIcon from '@mui/icons-material/Instagram';
import FacebookIcon from '@mui/icons-material/Facebook';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import ChatIcon from '@mui/icons-material/Chat';
import PublicIcon from '@mui/icons-material/Public';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';

import LogoSection from '../LogoSection/LogoSection'; // Ensure you import LogoSection correctly

const drawerWidth = 240;
const collapsedWidth = 90;

export default function CollapsibleSidebar({ open, handleToggleDrawer }) {
  return (
    <Drawer
      variant="permanent"
      open={open}
      sx={{
        width: open ? drawerWidth : collapsedWidth,
        transition: 'width 0.3s ease',
        '& .MuiDrawer-paper': {
          justifyContent: 'center',
          width: open ? drawerWidth : collapsedWidth,
          overflowX: 'hidden',
          backgroundColor: '#ffffff', // Adjust as needed
          color: 'Black',
          borderRight: 'none'
        }
      }}
    >
      <IconButton
        onClick={handleToggleDrawer}
        sx={{
          color: 'grey',
          alignSelf: 'flex-end'
        }}
      >
        {open ? <ChevronLeftIcon /> : <MenuIcon />}
      </IconButton>
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '100%'
        }}
      >
        <LogoSection />  // Integrate LogoSection here
        <List>
          {[
            { text: 'Top Stocks', icon: <DashboardIcon /> },
            { text: 'Data Bits', icon: <TrendingUpIcon /> },
            { text: 'Reddit Mentions', icon: <CommentIcon /> },
            { text: 'News Mentions', icon: <NewspaperIcon /> },
            { text: '4chan Mentions', icon: <EmojiEventsIcon /> },
            { text: 'Instagram Followers', icon: <InstagramIcon /> },
            { text: 'Facebook Followers', icon: <FacebookIcon /> },
            { text: 'TikTok Followers', icon: <MusicNoteIcon /> },
            { text: 'Threads Followers', icon: <ChatIcon /> },
            { text: 'Webpage Traffic', icon: <PublicIcon /> }
          ].map((item, index) => (
            <ListItemButton key={index} sx={{ display: 'flex', justifyContent: 'center' }}>
              <ListItemIcon>{item.icon}</ListItemIcon>
              <ListItemText primary={item.text} sx={{ display: open ? 'block' : 'none' }} />
            </ListItemButton>
          ))}
        </List>
      </Box>
    </Drawer>
  );
}
