import React from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';  // Import ListItemIcon
import ListItemText from '@mui/material/ListItemText';
import { Box } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';  // Import appropriate icons
import QueryStatsIcon from '@mui/icons-material/QueryStats';
import ChatIcon from '@mui/icons-material/Chat';
import NewspaperIcon from '@mui/icons-material/Newspaper';
import GroupIcon from '@mui/icons-material/Group';
import FacebookIcon from '@mui/icons-material/Facebook';
import EmailIcon from '@mui/icons-material/Email';
import PublicIcon from '@mui/icons-material/Public';
import trade from './ASSETS/trade.jpg';

const drawerWidth = 240;

const Sidebar = ({ open, onClose }) => {
  const icons = [<HomeIcon />, <QueryStatsIcon />, <ChatIcon />, <NewspaperIcon />, <EmailIcon />, <GroupIcon />, <FacebookIcon />, <TikTokIcon />, <EmailIcon />, <PublicIcon />];

  return (
    <Drawer
      variant="persistent"
      open={open}
      onClose={onClose}
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
          backgroundColor: 'primary.main',
          color: 'white'
        },
      }}
    >
      <Box sx={{ textAlign: 'center', p: 2 }}>
        <img src={trade} alt="Logo" style={{ width: '50%', margin: '0 auto' }} />
      </Box>
      <List>
        {['Top Stocks', 'Data Bits', 'Reddit Mentions', 'News Mentions', '4chan Mentions', 'Instagram Followers', 'Facebook Followers', 'TikTok Followers', 'Threads Followers', 'Webpage Traffic'].map((text, index) => (
          <ListItem key={text} disablePadding>
            <ListItemButton>
              <ListItemIcon>
                {icons[index]}
              </ListItemIcon>
              <ListItemText primary={text} sx={{ display: open ? 'block' : 'none' }} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Drawer>
  );
};

export default Sidebar;
