import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginForm from './components/signin/signin';
import Register from './pages/Register';
import Layout from './components/Layout/Layout';
import AppRoutes from './AppRoutes'; // Import des routes configurées

const AppRouter = ({ mode, handleModeChange }) => {
  const userRole = "employee"; // Constante pour le rôle de l'utilisateur

  return (
    <Router>
      <Routes>
        {/* Route pour la page de connexion */}
        <Route path="/" element={<LoginForm mode={mode} handleModeChange={handleModeChange} />} />

        {/* Route pour la page d'enregistrement */}
        <Route path="/register" element={<Register />} />

        {/* Boucle sur les routes supplémentaires définies dans AppRoutes */}
        {AppRoutes.map((route, index) => (
          <Route key={index} path={route.path} element={
            <Layout role={userRole} mode={mode} handleModeChange={handleModeChange}>
              {route.element}
            </Layout>
          } />
        ))}
      </Routes>
    </Router>
  );
};

export default AppRouter;
