import React, { useState } from 'react';
import { ThemeProvider, useTheme } from '@mui/material/styles';
import {
  AppBar, Box, Toolbar, MenuItem, Button, IconButton, Typography
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Sidebar from '../SideBar/SideBar'; // Vérifiez le chemin
import { useNavigate } from 'react-router-dom';

const drawerWidth = 240;
const pages = ['Datasets', 'Top Stocks', 'Stock Alerts', 'AI Stock Picks'];

export default function Layout({ children, showSidebar = true, showAppBar = true, isAuthenticated }) {
  const theme = useTheme();
  const [sidebarOpen, setSidebarOpen] = useState(true); // L'état initial indique que le sidebar est ouvert
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen); // Cette fonction bascule l'état d'ouverture du sidebar
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex', width: '100%', backgroundColor: '#F0F0F2', overflow: 'auto' }}>
        {showAppBar && (
          <AppBar position="fixed" sx={{
            zIndex: theme.zIndex.drawer + 1,
            width: `calc(100% - ${sidebarOpen ? drawerWidth : 0}px)`,
            marginLeft: `${sidebarOpen ? drawerWidth : 0}px`,
            transition: theme.transitions.create(['margin', 'width'], {
              easing: theme.transitions.easing.sharp,
              duration: theme.transitions.duration.leavingScreen,
            }),
          }}>
            <Toolbar>
              {showSidebar && (
                <IconButton
                  color="inherit"
                  aria-label="open drawer"
                  onClick={toggleSidebar}
                  sx={{ mr: 2 }}
                >
                  <MenuIcon />
                </IconButton>
              )}
              {/* Autres éléments de la barre d'outils */}
            </Toolbar>
          </AppBar>
        )}
        {showSidebar && <Sidebar open={sidebarOpen} toggleSidebar={toggleSidebar} />}
        {/* Autres éléments de l'interface utilisateur */}
      </Box>
    </ThemeProvider>
  );
}
