import React from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import HomeIcon from '@mui/icons-material/Home';
import NewsIcon from '@mui/icons-material/Article';
import { Box } from '@mui/material';

const drawerWidth = 240;
const iconWidth = 56; // Largeur pour juste les icônes

const Sidebar = ({ open }) => {
  return (
    <Drawer
      variant="permanent"
      open={open}
      sx={{
        width: open ? drawerWidth : iconWidth,
        '& .MuiDrawer-paper': {
          width: open ? drawerWidth : iconWidth,
          boxSizing: 'border-box',
          transition: 'width 0.3s ease'
        },
      }}
    >
      <Box
        sx={{ textAlign: 'center', p: 2, backgroundColor: 'primary.main', color: 'white' }}
        style={{ display: open ? 'block' : 'none' }}
      >
        Menu
      </Box>
      <List>
        {[{ text: 'Home', icon: <HomeIcon /> }, { text: 'News Mentions', icon: <NewsIcon /> }].map((item) => (
          <ListItem key={item.text} disablePadding>
            <ListItemButton>
              <ListItemIcon>
                {item.icon}
              </ListItemIcon>
              <ListItemText primary={item.text} style={{ display: open ? 'block' : 'none' }} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Drawer>
  );
};

export default Sidebar;
