import React from "react";
import { ThemeProvider, useTheme } from '@mui/material/styles';
import { AppBar, Box, Grid, Toolbar, Typography, MenuItem, Link } from '@mui/material';

// Assuming the definition of `theme` and `pages` somewhere in your project
const theme = useTheme(); // This should be defined accordingly
const pages = ['Home', 'About', 'Contact']; // Example pages

export default function Layout({ children, role, mode, handleModeChange }) {
    
    const handleMobileMenuOpen = (event) => {
        setMobileMoreAnchorEl(event.currentTarget);
      };
    

    return (
        <ThemeProvider theme={theme}>
            <Box sx={{
                display: 'flex',
                height: "100vh",
                overflow: 'hidden', // Corrected for valid CSS value
                scrollbarWidth: 'none',
                '&::-webkit-scrollbar': {
                    width: '0',
                }
            }}>
                <Box component="main" sx={{
                    backgroundColor: (theme) => theme.palette.mode === 'light' ? "#fff" : "#000",
                    flexGrow: 1,
                    height: '100%',
                    overflow: 'hidden', // Corrected for valid CSS value
                    scrollbarWidth: 'none',
                    '&::-webkit-scrollbar': {
                        width: '0',
                    }
                }}>
                    <AppBar position="static" sx={{ height: '40px', background: `linear-gradient(135deg, #000000, #1e222d)` }}>
                        <Toolbar sx={{ minHeight: '40px !important' }}> {/* Corrected minHeight to apply correctly */}
                            <Link to="/waiting">
                                <Typography
                                    variant="h5"
                                    noWrap
                                    component="div"
                                    sx={{
                                        display: { xs: 'none', sm: 'block' },
                                        mb: 3, mr: 5,
                                        fontFamily: 'Orbitron',
                                        textShadow: "0px 0px 5px rgb(255,255,255)",
                                        color: "white"
                                    }}
                                >
                                    TradingEra
                                </Typography>
                            </Link>
                            {pages.map((page) => (
                                <MenuItem key={page} component={Link} to={`/${page}`} sx={{
                                    fontSize: '13px',
                                    textDecoration: 'none',
                                    color: 'inherit',
                                    mb: 2.5,
                                    '&:hover': {
                                        borderRadius: '4px',
                                        height: 25,
                                        textShadow: "0px 0px 5px rgb(255,255,255)",
                                        color: 'white'
                                    }
                                }}>
                                    <Typography variant="body1" textAlign="center" sx={{ fontFamily: 'Orbitron' }}>{page}</Typography>
                                </MenuItem>
                            ))}
                            <Box sx={{ flexGrow: 1 }} />
                        </Toolbar>
                    </AppBar>
                </Box>
            </Box>
        </ThemeProvider>
    );
}
