// components/CustomSidebar.js
import React from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, Collapse } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import ChatIcon from '@mui/icons-material/Chat';
import AnnouncementIcon from '@mui/icons-material/Announcement';
import PublicIcon from '@mui/icons-material/Public';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import EmailIcon from '@mui/icons-material/Email';
import TrafficIcon from '@mui/icons-material/Traffic';
import { ExpandLess, ExpandMore } from '@mui/icons-material';

const CustomSidebar = ({ sidebarOpen }) => {
    const [openSubMenu, setOpenSubMenu] = React.useState(false);

    const handleClick = () => {
        setOpenSubMenu(!openSubMenu);
    };

    const menuItems = [
        { text: 'Top Stocks', icon: <TrendingUpIcon />, onClick: handleClick, subMenuOpen: openSubMenu },
        { text: 'Data Bits', icon: <HomeIcon /> },
        { text: 'Reddit Mentions', icon: <ChatIcon /> },
        { text: 'News Mentions', icon: <AnnouncementIcon /> },
        { text: '4chan Mentions', icon: <PublicIcon /> },
        { text: 'Instagram Followers', icon: <ThumbUpIcon /> },
        { text: 'Facebook Followers', icon: <ThumbUpIcon /> },
        { text: 'TikTok Followers', icon: <MusicNoteIcon /> },
        { text: 'Threads Followers', icon: <EmailIcon /> },
        { text: 'Webpage Traffic', icon: <TrafficIcon /> }
    ];

    return (
        <Drawer
            variant="permanent"
            sx={{
                width: sidebarOpen ? 240 : 60,
                flexShrink: 0,
                '& .MuiDrawer-paper': {
                    width: sidebarOpen ? 240 : 60,
                    boxSizing: 'border-box',
                    overflowX: 'hidden',  // Hide text when sidebar is closed
                    backgroundColor: '#ffffff', // Same as AppBar
                },
            }}
            open={sidebarOpen}
        >
            <List>
                {menuItems.map((item, index) => (
                    <React.Fragment key={item.text}>
                        <ListItem button onClick={item.onClick || (() => {})}>
                            <ListItemIcon>{item.icon}</ListItemIcon>
                            {sidebarOpen && <ListItemText primary={item.text} />}
                            {item.subMenuOpen ? <ExpandLess /> : (sidebarOpen && item.subMenuOpen !== undefined) && <ExpandMore />}
                        </ListItem>
                        {item.subMenuOpen && (
                            <Collapse in={openSubMenu} timeout="auto" unmountOnExit>
                                <List component="div" disablePadding>
                                    <ListItem button sx={{ pl: 4 }}>
                                        <ListItemText primary="Sub Option 1" />
                                    </ListItem>
                                    <ListItem button sx={{ pl: 4 }}>
                                        <ListItemText primary="Sub Option 2" />
                                    </ListItem>
                                </List>
                            </Collapse>
                        )}
                    </React.Fragment>
                ))}
            </List>
        </Drawer>
    );
};

export default CustomSidebar;
