import React, { useState, useEffect } from 'react';

const StockPrice = ({ symbol }) => {
  const [price, setPrice] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const apiKey = 'FFTZ06W4JZ6C7ZDS'; // API Key hardcoded
    const apiURL = `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${symbol}&interval=5min&apikey=${apiKey}`;

    fetch(apiURL)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const timeSeries = data['Time Series (5min)'];
        if (timeSeries) {
          const latestDatetime = Object.keys(timeSeries).reduce((a, b) => a > b ? a : b);
          const latestPrice = timeSeries[latestDatetime]['4. close'];
          setPrice(latestPrice);
        } else {
          setError('No data available');
        }
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, [symbol]);  // Dependencies

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>{symbol} Price</h1>
      <p>${price}</p>
    </div>
  );
};

export default TopRanked;
