import React, { useState } from "react";
import { ThemeProvider, useTheme } from '@mui/material/styles';
import { AppBar, Box, Toolbar, Typography, MenuItem, Link } from '@mui/material';

// Define `pages` and assume `theme` is passed as a prop or defined within ThemeProvider context higher up
const pages = ['Home', 'About', 'Contact'];

export default function Layout({ children, role, mode, handleModeChange }) {
    const theme = useTheme();
    const [anchorEl, setAnchorEl] = useState(null);
    const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);

    const handleMobileMenuOpen = (event) => {
        setMobileMoreAnchorEl(event.currentTarget);
    };

    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    return (
        <ThemeProvider theme={theme}>
            <Box sx={{
                display: 'flex',
                height: "100vh",
                overflow: 'hidden',
                scrollbarWidth: 'none',
                '&::-webkit-scrollbar': {
                    width: '0',
                }
            }}>
                <Box component="main" sx={{
                    backgroundColor: (theme) => theme.palette.mode === 'light' ? "#fff" : "#000",
                    flexGrow: 1,
                    height: '100%',
                    overflow: 'hidden',
                    scrollbarWidth: 'none',
                    '&::-webkit-scrollbar': {
                        width: '0',
                    }
                }}>
                    <AppBar position="static" sx={{ background: `linear-gradient(135deg, #000000, #1e222d)` }}>
                        <Toolbar>
                            <Link to="/waiting" sx={{ flexGrow: 1, display: 'flex', alignItems: 'center' }}>
                                <Typography
                                    variant="h5"
                                    noWrap
                                    component="div"
                                    sx={{
                                        display: { xs: 'none', sm: 'block' },
                                        fontFamily: 'Orbitron',
                                        textShadow: "0px 0px 5px rgb(255,255,255)",
                                        color: "white"
                                    }}
                                >
                                    TradingEra
                                </Typography>
                            </Link>
                            {pages.map((page) => (
                                <MenuItem key={page} component={Link} to={`/${page}`} sx={{
                                    fontSize: '13px',
                                    textDecoration: 'none',
                                    color: 'inherit',
                                    '&:hover': {
                                        borderRadius: '4px',
                                        textShadow: "0px 0px 5px rgb(255,255,255)",
                                        color: 'white'
                                    }
                                }}>
                                    <Typography textAlign="center" sx={{ fontFamily: 'Orbitron' }}>{page}</Typography>
                                </MenuItem>
                            ))}
                        </Toolbar>
                    </AppBar>
                    {children}  {/* Render children passed to Layout */}
                </Box>
            </Box>
        </ThemeProvider>
    );
}
