import React, { useState } from 'react';
import { ThemeProvider, useTheme } from '@mui/material/styles';
import {
  AppBar, Box, Toolbar, MenuItem, Button, Link, IconButton, TextField, InputAdornment,
  Grid, Typography
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Sidebar from '../SideBar/SideBar'; // Vérifiez que ce chemin est correct

const drawerWidth = 240;
const pages = ['Datasets', 'Top Stocks', 'Stock Alerts', 'AI Stock Picks'];

export default function Layout({ children, showSidebar, showAppBar, isAuthenticated }) {
  const theme = useTheme();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex' }}>
        {showSidebar && <Sidebar open={sidebarOpen} onClose={toggleSidebar} />}
        <AppBar position="fixed" sx={{ width: `calc(100% - ${sidebarOpen ? drawerWidth : 0}px)`, ml: `${sidebarOpen ? `${drawerWidth}px` : 0}` }}>
          <Toolbar>
            <IconButton
              size="large"
              edge="start"
              color="inherit"
              aria-label="open drawer"
              onClick={toggleSidebar}
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
            <TextField
              variant="outlined"
              placeholder="Search Stocks & Crypto"
              sx={{ flex: 1, bgcolor: 'background.paper', borderRadius: 1 }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
            {pages.map((page) => (
              <MenuItem 
                key={page}
                onClick={() => navigate(`/${page.replace(/\s+/g, '').toLowerCase()}`)}
              >
                {page}
              </MenuItem >
            ))}
          </Toolbar>
        </AppBar>
        <Box
          component="main"
          sx={{
            flexGrow: 1,
            p: 3,
            width: `calc(100% - ${sidebarOpen ? drawerWidth : 0}px)`,
            ml: `${sidebarOpen ? `${drawerWidth}px` : 0}`,
          }}
        >
          {children}
        </Box>
      </Box>
    </ThemeProvider>
  );
}
