// AppRouter.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginForm from './components/signin/signin';
import Register from './pages/Register';
import AppRoutes from './AppRoutes';
import Layout from './components/Layout/Layout';

const AppRouter = ({ mode, handleModeChange }) => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={
          <Layout sidebar={false}>
            <LoginForm mode={mode} handleModeChange={handleModeChange} />
          </Layout>
        } />
        <Route path="/register" element={
          <Layout sidebar={false}>
            <Register />
          </Layout>
        } />
        {AppRoutes.map((route, index) => (
          <Route key={index} path={route.path} element={
            <Layout sidebar={route.sidebar}>  // Assuming each route config has a sidebar boolean
              {React.cloneElement(route.element, { mode: mode, handleModeChange: handleModeChange })}
            </Layout>
          } />
        ))}
      </Routes>
    </Router>
  );
};

export default AppRouter;
