import React from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton, Box } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import HomeIcon from '@mui/icons-material/Home';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import AnnouncementIcon from '@mui/icons-material/Announcement';
import PublicIcon from '@mui/icons-material/Public';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import EmailIcon from '@mui/icons-material/Email';
import TrafficIcon from '@mui/icons-material/Traffic';

const CustomSidebar = ({ sidebarOpen, setSidebarOpen }) => {
    // Function to toggle sidebar
    const handleToggleSidebar = () => {
        setSidebarOpen(!sidebarOpen);
    };

    const menuItems = [
        { text: 'Top Stocks', icon: <TrendingUpIcon /> },
        { text: 'Data Bits', icon: <HomeIcon /> },
        { text: 'Reddit Mentions', icon: <ChatBubbleOutlineIcon /> },
        { text: 'News Mentions', icon: <AnnouncementIcon /> },
        { text: '4chan Mentions', icon: <PublicIcon /> },
        { text: 'Instagram Followers', icon: <ThumbUpIcon /> },
        { text: 'Facebook Followers', icon: <ThumbUpIcon /> },
        { text: 'TikTok Followers', icon: <MusicNoteIcon /> },
        { text: 'Threads Followers', icon: <EmailIcon /> },
        { text: 'Webpage Traffic', icon: <TrafficIcon /> }
    ];

    return (
        <Box sx={{ width: sidebarOpen ? 240 : 60 }}>
            <IconButton 
                onClick={handleToggleSidebar} 
                sx={{ color: 'white', margin: '10px', padding: '10px', display: 'block', backgroundColor: '#333', ':hover': { backgroundColor: '#444' } }}
            >
                <MenuIcon />
            </IconButton>
            <Drawer
                variant="permanent"
                open={true}  // Drawer is always rendered but its width changes
                sx={{
                    width: sidebarOpen ? 240 : 60,
                    flexShrink: 0,
                    '& .MuiDrawer-paper': {
                        width: sidebarOpen ? 240 : 60,
                        boxSizing: 'border-box',
                        overflowX: 'hidden',  // Hide text when sidebar is closed
                        backgroundColor: '#333',  // Dark background color
                        color: 'white',
                        transition: 'width 225ms cubic-bezier(0.4, 0, 0.6, 1) 0ms',  // Smooth transition for width
                    },
                }}
            >
                <List disablePadding>
                    {menuItems.map((item) => (
                        <ListItem button key={item.text}>
                            <ListItemIcon sx={{ color: 'white' }}>{item.icon}</ListItemIcon>
                            {sidebarOpen && <ListItemText primary={item.text} />}
                        </ListItem>
                    ))}
                </List>
            </Drawer>
        </Box>
    );
};

export default CustomSidebar;
