import React, { useState } from 'react';
import { Box, CssBaseline } from '@mui/material';
import CollapsibleSidebar from '../SideBar/'; // Ensure this is imported correctly
import CustomAppBar from '../AppBar/AppBar'; // Ensure this is imported correctly

const MainLayout = () => {
    const [sidebarOpen, setSidebarOpen] = useState(false);

    const handleSidebarToggle = () => {
        setSidebarOpen(!sidebarOpen);
    };

    return (
        <Box sx={{ display: 'flex', height: '100vh', width: '100vw' }}>
            <CssBaseline />
            <CustomAppBar handleSidebarToggle={handleSidebarToggle} sidebarOpen={sidebarOpen} />
            <CollapsibleSidebar open={sidebarOpen} handleToggleDrawer={handleSidebarToggle} />
            <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                {/* Main content goes here, you can add routing or direct content */}
                <h1>Welcome to AltIndex</h1>
                <p>This is the main content area. Add more content or components here as needed.</p>
            </Box>
        </Box>
    );
};

export default MainLayout;
