import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Toolbar from '@mui/material/Toolbar';
import ResponsiveAppBar from '../AppBar/AppBar'; // Assurez-vous que ce chemin mène à votre fichier ResponsiveAppBar
import Sidebar from '../SideBar/SideBar'; // Assurez-vous que ce chemin mène à votre fichier Sidebar

const Layout = ({ children }) => {
    const [mobileOpen, setMobileOpen] = useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    };

    return (
        <Box sx={{ display: 'flex' }}>
            <ResponsiveAppBar handleDrawerToggle={handleDrawerToggle} />
            <Sidebar open={mobileOpen} onClose={() => setMobileOpen(false)} />
            <Box
                component="main"
                sx={{
                    flexGrow: 1,
                    p: 3,
                    width: { sm: mobileOpen ? `calc(100% - 240px)` : `100%` }, // Ajustez la largeur en fonction de l'état du Sidebar
                    transition: theme => theme.transitions.create('width', {
                        easing: theme.transitions.easing.sharp,
                        duration: theme.transitions.duration.enteringScreen,
                    }),
                }}
            >
                <Toolbar /> {/* Ajout d'un Toolbar pour décaler le contenu en dessous de l'AppBar */}
                <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
                    {children} {/* Ici, le reste du contenu de votre application sera rendu */}
                </Container>
            </Box>
        </Box>
    );
};

export default Layout;
