import React, { useState } from 'react';
import { ThemeProvider, useTheme } from '@mui/material/styles';
import {
  AppBar, Box, Toolbar, MenuItem, IconButton, Typography
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import Sidebar from '../Sidebar'; // Assurez-vous que le chemin est correct
import { useNavigate } from 'react-router-dom';

const drawerWidth = 240;
const pages = ['Datasets', 'Top Stocks', 'Stock Alerts', 'AI Stock Picks'];

export default function Layout({ children }) {
  const theme = useTheme();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex', width: '100%', height: '100vh' }}>
        <AppBar position="fixed" sx={{
          zIndex: theme.zIndex.drawer + 1,
          width: `calc(100% - ${sidebarOpen ? drawerWidth : 70}px)`, // 70px is the collapsed width
          transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
          }),
        }}>
          <Toolbar>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              onClick={toggleSidebar}
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
            {pages.map((page) => (
              <MenuItem key={page} onClick={() => navigate(`/${page.toLowerCase().replace(/\s+/g, '')}`)}>
                {page}
              </MenuItem>
            ))}
          </Toolbar>
        </AppBar>
        <Sidebar open={sidebarOpen} toggleSidebar={toggleSidebar} />
        <Box
          component="main"
          sx={{
            flexGrow: 1,
            p: 3,
            marginLeft: `${sidebarOpen ? drawerWidth : 70}px`, // Same here for the collapsed width
            transition: theme.transitions.create(['margin'], {
              easing: theme.transitions.easing.sharp,
              duration: theme.transitions.duration.leavingScreen,
            }),
          }}
        >
          {children}
        </Box>
      </Box>
    </ThemeProvider>
  );
}
