import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, IconButton, Button, Menu, MenuItem } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircle from '@mui/icons-material/AccountCircle';

const CustomAppBar = ({ handleSidebarToggle, sidebarOpen }) => {
    const [anchorEl, setAnchorEl] = useState(null);
    const isMenuOpen = Boolean(anchorEl);

    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };

    return (
        <AppBar position="static" sx={{ color: 'black', backgroundColor: '#ffffff', minHeight: '50px' }}>
            <Toolbar>
                <IconButton
                    edge="start"
                    color="inherit"
                    aria-label="open drawer"
                    onClick={handleSidebarToggle}
                    sx={{ mr: 2, display: sidebarOpen ? 'none' : 'inline-flex' }}
                >
                    <MenuIcon />
                </IconButton>
                <Typography variant="h6" noWrap component="div" sx={{ flexGrow: 1, fontFamily: 'Roboto', fontWeight: 500, marginLeft: sidebarOpen ? '0px' : '48px' }}>
                    AltIndex
                </Typography>
                <Button color="inherit">Datasets</Button>
                <Button color="inherit">Top Stocks</Button>
                <Button color="inherit">Stock Alerts</Button>
                <Button color="inherit">AI Stock Picks</Button>
                <IconButton
                    edge="end"
                    aria-label="account of current user"
                    aria-haspopup="true"
                    onClick={handleProfileMenuOpen}
                    color="inherit"
                >
                    <AccountCircle />
                </IconButton>
                <Menu
                    id="profile-menu"
                    anchorEl={anchorEl}
                    open={isMenuOpen}
                    onClose={handleMenuClose}
                >
                    <MenuItem onClick={handleMenuClose}>Profile</MenuItem>
                    <MenuItem onClick={handleMenuClose}>My account</MenuItem>
                </Menu>
            </Toolbar>
        </AppBar>
    );
};

export default CustomAppBar;
