import React, { useEffect, useState } from 'react';
import { Grid, Typography, Divider, CircularProgress } from '@mui/material';

const CompanyInfo = ({ ticker }) => {

   
        // État pour stocker le nom de l'entreprise
        const [companyName, setCompanyName] = useState('');
      
        // Fonction pour gérer le clic sur une entreprise
        const handleCompanyClick = (name) => {
          setCompanyName(name); // Mettre à jour l'état avec le nouveau nom
        };

  return (
    <Grid container sx={{ maxWidth: 345, margin: 'auto', mt: 5 }}>
      <Grid item>
        <Typography variant="h5" component="div" sx={{ fontWeight: 'bold', mb: 2 }}>
          about {companyName}
        </Typography>

        <Typography variant="subtitle1" gutterBottom component="div">
          Price: ${}
        </Typography>
        <Divider />

        <Typography variant="subtitle1" gutterBottom component="div" sx={{ mt: 2 }}>
          Year Range: {}
        </Typography>
        <Divider />

        <Typography variant="subtitle1" gutterBottom component="div" sx={{ mt: 2 }}>
          Target Price: ${}
        </Typography>
        <Divider />

        <Typography variant="subtitle1" gutterBottom component="div" sx={{ mt: 2 }}>
          Volume: {}
        </Typography>
        <Divider />

        <Typography variant="subtitle1" gutterBottom component="div" sx={{ mt: 2 }}>
          Market Cap: ${}B
        </Typography>
        <Divider />

        <Typography variant="subtitle1" gutterBottom component="div" sx={{ mt: 2 }}>
          Dividend Yield: {}
        </Typography>
      </Grid>
    </Grid>
  );
};

export default CompanyInfo;
