// components/Layout.js
import React, { useState } from 'react';
import { Box } from '@mui/material';
import CustomAppBar from './CustomAppBar';
import CustomSidebar from '../';

const Layout = ({ children }) => {
    const [sidebarOpen, setSidebarOpen] = useState(true);

    const handleSidebarToggle = () => {
        setSidebarOpen(!sidebarOpen);
    };

    return (
        <Box sx={{ display: 'flex' }}>
            <CustomAppBar handleSidebarToggle={handleSidebarToggle} />
            <CustomSidebar sidebarOpen={sidebarOpen} handleSidebarToggle={handleSidebarToggle} />
            <Box component="main" sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${sidebarOpen ? 240 : 60}px)` } }}>
                {children}
            </Box>
        </Box>
    );
};

export default Layout;
