import React, { useState } from 'react';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import ResponsiveAppBar from '../AppBar/AppBarResponsiveAppBar'; // Make sure this points to the location of your ResponsiveAppBar file
import Sidebar from './Sidebar'; // Make sure this points to the location of your Sidebar file

const Layout = ({ children }) => {
    const [mobileOpen, setMobileOpen] = useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    };

    return (
        <Box sx={{ display: 'flex' }}>
            <ResponsiveAppBar handleDrawerToggle={handleDrawerToggle} />
            <Sidebar open={mobileOpen} onClose={() => setMobileOpen(false)} />
            <Box
                component="main"
                sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - 240px)` } }}
            >
                <Toolbar /> {/* Ensure Toolbar is imported to offset the content below the AppBar */}
                <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
                    {children} {/* This is where you would render the rest of your app's content */}
                </Container>
            </Box>
        </Box>
    );
};

export default Layout;
