import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './components/signin/signin';
import Register from './pages/Register';
import Vitrine from './components/Vitrine/'; // Importer le composant Vitrine
import AppRoutes from './AppRoutes';
import Layout from './components/Layout/Layout';

const AppRouter = ({ mode, handleModeChange }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Exemple de vérification d'authentification
  useEffect(() => {
    // Vous pouvez remplacer cette logique par une vérification réelle de l'authentification
    const authStatus = localStorage.getItem('isAuthenticated') === 'true';
    setIsAuthenticated(authStatus);
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={isAuthenticated ? <Navigate to="/dashboard" /> : <Vitrine />} />
        <Route path="/signin" element={
          <Layout showSidebar={false} showAppBar={false} isAuthenticated={isAuthenticated}>
            <LoginForm mode={mode} handleModeChange={handleModeChange} setIsAuthenticated={setIsAuthenticated} />
          </Layout>
        } />
        <Route path="/register" element={
          <Layout showSidebar={false} showAppBar={false} isAuthenticated={isAuthenticated}>
            <Register />
          </Layout>
        } />
        {AppRoutes.map((route, index) => (
          <Route key={index} path={route.path} element={
            isAuthenticated ? (
              <Layout showSidebar={route.sidebar} showAppBar={true} isAuthenticated={isAuthenticated}>
                {React.cloneElement(route.element, { mode: mode, handleModeChange: handleModeChange })}
              </Layout>
            ) : (
              <Navigate to="/" />
            )
          } />
        ))}
      </Routes>
    </Router>
  );
};

export default AppRouter;
