// components/CustomSidebar.js
import React, { useState } from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import ChatIcon from '@mui/icons-material/Chat';
import AnnouncementIcon from '@mui/icons-material/Announcement';
import PublicIcon from '@mui/icons-material/Public';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
import MusicNoteIcon from '@mui/icons-material/MusicNote';
import EmailIcon from '@mui/icons-material/Email';
import TrafficIcon from '@mui/icons-material/Traffic';
const drawerWidth = 240;
const drawerWidthClosed = 60; // Width to keep icons visible
const CustomSidebar = ({ sidebarOpen, handleSidebarToggle }) => {
    const [open, setOpen] = useState(true);

    const handleDrawerToggle = () => {
        setOpen(!open);
    };
    const DrawerHeader = styled('div')(({ theme }) => ({
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
      }));
    const menuItems = [
        { text: 'Top Stocks', icon: <TrendingUpIcon /> },
        { text: 'Data Bits', icon: <HomeIcon /> },
        { text: 'Reddit Mentions', icon: <ChatIcon /> },
        { text: 'News Mentions', icon: <AnnouncementIcon /> },
        { text: '4chan Mentions', icon: <PublicIcon /> },
        { text: 'Instagram Followers', icon: <ThumbUpIcon /> },
        { text: 'Facebook Followers', icon: <ThumbUpIcon /> },
        { text: 'TikTok Followers', icon: <MusicNoteIcon /> },
        { text: 'Threads Followers', icon: <EmailIcon /> },
        { text: 'Webpage Traffic', icon: <TrafficIcon /> }
    ];

    return (
        <Drawer
            variant="permanent"
            sx={{
                width: sidebarOpen ? 240 : 60,
                flexShrink: 0,
                '& .MuiDrawer-paper': {
                    width: sidebarOpen ? 240 : 60,
                    boxSizing: 'border-box',
                    overflowX: 'hidden',
                    backgroundColor: '#ffffff',
                },
            }}
            open={sidebarOpen}
        >
            <List>
                {menuItems.map((item, index) => (
                    <ListItem button key={item.text} onClick={handleSidebarToggle}>
                        <ListItemIcon>{item.icon}</ListItemIcon>
                        {sidebarOpen && <ListItemText primary={item.text} />}
                    </ListItem>
                ))}
            </List>
        </Drawer>
    );
};

export default CustomSidebar;
