import React, { useState } from 'react';
import { ThemeProvider, useTheme } from '@mui/material/styles';
import {
  AppBar, Box, Toolbar, MenuItem, Button, Typography, IconButton, Menu,
  TextField, InputAdornment, useNavigate
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Sidebar from './Sidebar/Sidebar'; // Adjust the import path as necessary

export default function Layout({ children, showSidebar, showAppBar, isAuthenticated }) {
  const theme = useTheme();
  const navigate = useNavigate(); // Hook for navigation
  const [anchorEl, setAnchorEl] = useState(null);
  const isMenuOpen = Boolean(anchorEl);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleNavigate = (path) => {
    navigate(path);
    handleMenuClose();
  };

  const pages = [
    { name: 'Datasets', path: '/datasets' },
    { name: 'Top Stocks', path: '/topranked' },
    { name: 'Stock Alerts', path: '/alerts' },
    { name: 'AI Stock Picks', path: '/aipicks' }
  ];

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1, display: 'flex' }}>
        {showAppBar && (
          <AppBar position="fixed" sx={{ zIndex: theme.zIndex.drawer + 1 }}>
            <Toolbar>
              {showSidebar && (
                <IconButton
                  size="large"
                  edge="start"
                  color="inherit"
                  aria-label="open drawer"
                  onClick={() => navigate('/')} // Adjust as needed for sidebar toggle or main navigation
                  sx={{ mr: 2 }}
                >
                  <MenuIcon />
                </IconButton>
              )}
              <TextField
                variant="outlined"
                placeholder="Search Stocks & Crypto"
                sx={{ flex: 1, bgcolor: 'background.paper', borderRadius: 1 }}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <SearchIcon />
                    </InputAdornment>
                  ),
                }}
              />
              {pages.map((page) => (
                <MenuItem key={page.name} onClick={() => handleNavigate(page.path)} sx={{ color: 'white', display: 'block' }}>
                  <Typography variant="h6" textAlign="center">{page.name}</Typography>
                </MenuItem>
              ))}
              <Box sx={{ flexGrow: 1 }} />
              {isAuthenticated ? (
                <IconButton
                  edge="end"
                  aria-label="account of current user"
                  aria-controls="primary-search-account-menu"
                  aria-haspopup="true"
                  onClick={handleProfileMenuOpen}
                  color="inherit"
                >
                  <AccountCircle />
                </IconButton>
              ) : (
                <Button color="inherit" onClick={() => navigate('/signin')}>Sign In</Button>
              )}
            </Toolbar>
          </AppBar>
        )}
        <Sidebar open={showSidebar} onClose={() => navigate('/')} /> {/* Adjust onClose as needed */}
        <Box component="main" sx={{ flexGrow: 1, p: 3, marginTop: showAppBar ? '64px' : '0px' }}>
          {children}
        </Box>
      </Box>
    </ThemeProvider>
  );
}
