import React from 'react';
import { AppBar, Toolbar, Typography, IconButton, MenuItem, Link, Box, Grid } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import MoreIcon from '@mui/icons-material/MoreVert';
import PersonOutlineOutlinedIcon from '@mui/icons-material/PersonOutlineOutlined';
import LogoutIcon from '@mui/icons-material/Logout';

const pages = ['Datasets', 'Top Stocks', 'Stock Alerts', 'AI Stock Picks'];

export default function CustomAppBar({ isTimerOn, handleLogoutClick }) {
  const navigate = useNavigate();

  return (
    <AppBar position="static" sx={{ background: 'linear-gradient(135deg, #000000, #1e222d)' }}>
      <Toolbar>
        <Link to="/" sx={{ display: { xs: 'none', sm: 'block' } }}>
          <Typography variant="h5" sx={{ fontFamily: 'Orbitron', color: 'white', textShadow: "0px 0px 5px rgb(255,255,255)" }}>
            TradingEra
          </Typography>
        </Link>

        {pages.map((page) => (
          <MenuItem key={page} onClick={() => navigate(`/${page.toLowerCase().replace(/\s+/g, '')}`)} sx={{ color: 'white', textShadow: "0px 0px 5px rgb(255,255,255)" }}>
            <Typography variant="h6" sx={{ fontFamily: 'Orbitron' }}>{page}</Typography>
          </MenuItem>
        ))}

        <Box sx={{ flexGrow: 1 }} />

        <IconButton
          onClick={handleLogoutClick}
          sx={{ visibility: isTimerOn ? 'visible' : 'hidden' }}
          color="inherit"
        >
          <LogoutIcon />
        </IconButton>

        <IconButton color="inherit">
          <PersonOutlineOutlinedIcon />
        </IconButton>

        <IconButton color="inherit">
          <MoreIcon />
        </IconButton>
      </Toolbar>
    </AppBar>
  );
}
