const getDesignTokens = (mode) => ({
    palette: {
      mode,
      ...(mode === 'light'
        ? {
            // Light theme colors
            primary: {
                main: "#007aff",  // Blue for primary buttons and links
                contrastText: "#ffffff", // White text on blue background
            },
            secondary: {
                main: "#ff9500", // Orange for secondary elements
                contrastText: "#ffffff", // White text on orange background
            },
            background: {
                default: "#f4f4f8", // Very light gray for page backgrounds
                paper: "#ffffff", // White for elements like cards and sheets
            },
            text: {
                primary: "#1a1a1a", // Dark gray for primary text
                secondary: "#686868", // Lighter gray for secondary text
            },
        }
        : {
            // Dark theme colors
            primary: {
                main: "#0a84ff", // Brighter blue for primary elements in dark mode
                contrastText: "#ffffff",
            },
            secondary: {
                main: "#ff9f0a", // Brighter orange for secondary elements in dark mode
                contrastText: "#ffffff",
            },
            background: {
                default: "#1c1c1e", // Almost black for page backgrounds
                paper: "#2c2c2e", // Slightly lighter black for elements like cards and sheets
            },
            text: {
                primary: "#ffffff", // White for primary text in dark mode
                secondary: "#b2b2b2", // Light gray for secondary text
            },
        }),
      },
      typography: {
        fontFamily: '"Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif',
        fontSize: 14,
      },
    });
    
  export default getDesignTokens;
