import React, { useState } from 'react';
import io from 'socket.io-client'; 
function StockData() {
    const [symbol, setSymbol] = useState('IBM');
    const [stockData, setStockData] = useState(null);

    const fetchData = () => {
        fetch(`http://localhost:5000/stock_data?symbol=${symbol}`)
            .then(response => response.json())
            .then(data => setStockData(data))
            .catch(error => console.error('Error fetching data:', error));
    };

    return (
        <div>
            <input 
                type="text" 
                value={symbol} 
                onChange={(e) => setSymbol(e.target.value.toUpperCase())} 
                placeholder="Enter Stock Symbol"
            />
            <button onClick={fetchData}>Get Stock Data</button>
            {stockData && (
                <div>
                    <p>Last Refreshed: {stockData.last_refreshed}</p>
                    <p>Last Close Price: {stockData.last_close_price} USD</p>
                    <p>Last Volume: {stockData.last_volume}</p>
                </div>
            )}
        </div>
    );
}

export default StockData;
